INSERT INTO sanpham (sanpham_ten, sanpham_mota, baohanh_id) 
VALUES
('Hệ thống văn phòng điện tử', 'Cung cấp và triển khai hệ thống văn phòng điện tử giúp quản lý hồ sơ và công việc một cách tự động, hiệu quả.',1),
('Nâng cấp phần mềm Quản lý thông tin sinh viên cho Hệ thống đào tạo trường X', 'Thực hiện nâng cấp phần mềm hiện tại, bổ sung các tính năng mới nhằm tối ưu hóa việc quản lý thông tin sinh viên.',2),
('Phát triển chức năng hỗ trợ đăng ký môn học trực tuyến cho sinh viên trường X', 'Phát triển chức năng mới trên hệ thống, cho phép sinh viên đăng ký môn học trực tuyến, thuận tiện và nhanh chóng.',3),
('Bổ sung hệ thống bảo mật dữ liệu cho Hệ thống Quản lý đào tạo trường X', 'Bổ sung các biện pháp bảo mật để đảm bảo an toàn dữ liệu, ngăn chặn truy cập trái phép và bảo vệ thông tin sinh viên.',4),
('Tích hợp hệ thống thanh toán học phí trực tuyến', 'Phát triển và tích hợp cổng thanh toán trực tuyến cho học phí, giúp sinh viên dễ dàng thực hiện thanh toán qua nhiều phương thức khác nhau.',5),
('Nâng cấp cơ sở hạ tầng máy chủ cho hệ thống quản lý đào tạo', 'Nâng cấp và mở rộng hạ tầng máy chủ để đáp ứng yêu cầu vận hành và quản lý khối lượng dữ liệu lớn của hệ thống đào tạo.',6),
('Bổ sung module quản lý thi trực tuyến cho sinh viên', 'Phát triển module mới cho phép tổ chức thi trực tuyến, tích hợp giám sát và chấm điểm tự động.',7),
('Cải thiện giao diện người dùng hệ thống đăng ký môn học', 'Thiết kế lại giao diện của hệ thống đăng ký môn học, giúp sinh viên dễ dàng thao tác và trải nghiệm mượt mà hơn.',8);
